function VerifyTextUnicodeCharacters(text)
{
    //Check if Text contains any Not allowed Chars 
    //Input: String to test
    //Output: Array first val is true if all is good secound is the not allowed chars
    var notSupportArray = [];
    for (var i = text.length - 1; i >= 0; i--)
        {
            var charCode = text.charCodeAt(i);
            // 0-9 48-57
            // A-Z 65-90 , 97-122
            // _ 95
            if ((charCode >= 48 && charCode <= 57) || (charCode >= 65 && charCode <= 90) || (charCode == 95) || (charCode >= 97 && charCode <= 122))
            {

            }
            else
            {
                notSupportArray.push(charCode);
            }
        }

    if (notSupportArray.length == 0)
        {
            return [true,""]
        }
    else
        {
            return [false,notSupportArray];
        }
}
